mapper.select:
SELECT rnum, k_no, k_title, k_view, k_replycnt, k_solutionyn, u_id, u_nick, k_name, k_blindyn FROM (SELECT ROW_NUMBER() OVER(ORDER BY k.k_no DESC) AS rnum, k.k_no,k.k_title, k.k_view, k.k_replycnt, k.k_solutionyn, k.k_blindyn, u.u_id, u.u_nick, (SELECT c_name FROM code WHERE c_code = UPPER(k.k_code)) AS k_name FROM (SELECT * FROM knowfit WHERE k_blindyn = 'N') k INNER JOIN userInfo u ON k.u_id = u.u_id
k_code = (SELECT c_code FROM CODE WHERE c_name = '자세교정') k_code = (SELECT c_code FROM CODE WHERE c_name = '식단') k_code = (SELECT c_code FROM CODE WHERE c_name = '운동') k_code = (SELECT c_code FROM CODE WHERE c_name = '고민') k_code = (SELECT c_code FROM CODE WHERE c_name = '기타')
) WHERE rnum BETWEEN #{param1} AND #{param2}
